mapper.select:
select * from claim


        clientId = (
        select id from client
        where name like concat('%', #{clientName}, '%')
        )


        and reasonId = (
        select id from reason
        where cause like concat('%', #{reasonName}, '%')
        )


        and reviewStatus = #{reviewStatus}


        and severity = #{severity}


        and Date(claimTime) = #{claimTime}


        and Date(reviewTime) = #{reviewTime}


        and externSeverity = #{externSeverity}


        and type = #{type}


        and claimerId = #{claimerId}


        and reviewerId = #{reviewerId}
